package org.varin.algorithms;

import static org.junit.Assert.assertEquals;

import org.junit.Test;

public class PalindromePartitioningTwoTest {

	@Test
	public void testMinCut() {
		assertEquals(2, new PalindromePartitioningTwo().minCut("geek"));
		assertEquals(0, new PalindromePartitioningTwo().minCut("aaaa"));
		assertEquals(1, new PalindromePartitioningTwo().minCut(
				""));
		assertEquals(42, new PalindromePartitioningTwo().minCut("eegiicgaeadbcfacfhifdbiehbgejcaeggcgbahfcajfhjjdgj"));
	}

}
